class SelectQuery {\n    private $query;\n\n    public function __construct($baseQuery) {\n        $this->query = $baseQuery;\n    }\n\n    public function setLimit($limit) {\n        $newQuery = clone $this;\n        $newQuery->query .= " LIMIT " . intval($limit);\n        return $newQuery;\n    }\n\n    public function addCriteria($criteria) {\n        $newQuery = clone $this;\n        $newQuery->query .= " AND " . $this->sanitize($criteria);\n        return $newQuery;\n    }\n\n    private function sanitize($value) {\n        // Implement proper sanitization here\n        return htmlspecialchars($value, ENT_QUOTES, 'UTF-8');\n    }\n\n    public function getQuery() {\n        return $this->query;\n    }\n}\n\n// Usage\nfunction getSomethingByName($name) {\n    $baseQuery = "SELECT * FROM table WHERE name = '" . htmlspecialchars($name, ENT_QUOTES, 'UTF-8') . "'";\n    return new SelectQuery($baseQuery);\n}\n\nfunction getResult(SelectQuery $selectObj) {\n    $query = $selectObj->getQuery();\n    // Execute the query and return the result\n    // For example: return $db->query($query)->fetchAll();\n}\n\n// Example usage\n$selectObj = getSomethingByName('example');\n$selectObj = $selectObj->setLimit(10);\n$selectObj = $selectObj->addCriteria('status = "active"');\n$result = getResult($selectObj);