function make_request($url) {\n    $url_parts = parse_url($url);\n    $host = $url_parts['host'];\n    $path = $url_parts['path'] . '?' . $url_parts['query'];\n    \n    $fp = fsockopen($host, 80, $errno, $errstr, 30);\n\n    if (!$fp) {\n        return false;\n    }\n\n    $out = "GET $path HTTP/1.1\r\n";\n    $out .= "Host: $host\r\n";\n    $out .= "Connection: Close\r\n\r\n";\n\n    fwrite($fp, $out);\n    fclose($fp);\n\n    return true;\n}\n\n// Example usage without waiting for output\nmake_request('http://www.externalsite.com/script1.php?variable=45');