$article_name = 'Aix-les-Bains'; // Replace with your desired article name\n$encoded_article_name = urlencode($article_name);\n\n$s = curl_init();\n\n$url = 'https://en.wikipedia.org/wiki/' . $encoded_article_name;\ncurl_setopt($s, CURLOPT_URL, $url);\ncurl_setopt($s, CURLOPT_HEADER, false);\ncurl_setopt($s, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($s, CURLOPT_USERAGENT, 'Mozilla/5.0 (compatible; yourbot/1.0; +http://yourwebsite.com/bot)');\n\n$html_content = curl_exec($s);\ncurl_close($s);\n\nif ($html_content === false) {\n    echo "Error fetching the content.";\n} else {\n    // Process or display the HTML content\n    echo $html_content;\n}