function write_ini_file($assoc_arr, $path, $has_sections = false) {\n    $content = "";\n    if ($has_sections) {\n        foreach ($assoc_arr as $key => $elem) {\n            $content .= "[" . $key . "]\n";\n            foreach ($elem as $key2 => $value) {\n                $content .= $key2 . " = \"" . $value . "\"\n";\n            }\n        }\n    } else {\n        foreach ($assoc_arr as $key => $value) {\n            $content .= $key . " = \"" . $value . "\"\n";\n        }\n    }\n\n    if (!$handle = fopen($path, 'w')) {\n        return false;\n    }\n    if (!fwrite($handle, $content)) {\n        return false;\n    }\n    fclose($handle);\n    return true;\n}\n\n// Example usage\n$config_data = [\n    'section1' => [\n        'key1' => 'value1',\n        'key2' => 'value2'\n    ],\n    'section2' => [\n        'keyA' => 'valueA',\n        'keyB' => 'valueB'\n    ]\n];\n\nwrite_ini_file($config_data, 'config.ini', true);