<?php\n    // Get the requested URL from the query parameter\n    $requested_url = isset($_GET['requested_url']) ? $_GET['requested_url'] : 'Unknown';\n\n    // Log the requested URL to a file\n    $log_file = '404_errors.log';\n    $log_entry = date('Y-m-d H:i:s') . " - Requested URL: " . $requested_url . "\n";\n    file_put_contents($log_file, $log_entry, FILE_APPEND | LOCK_EX);\n\n    // Display a custom 404 message (optional)\n    echo "<h1>404 Not Found</h1>";\n    echo "<p>The page you are looking for could not be found.</p>";\n    ?>