$descriptorspec = array(\n    0 => array("pipe", "r"),\n    1 => array("pipe", "w"),\n    2 => array("pipe", "w")\n);\n$process = proc_open('your_command_here', $descriptorspec, $pipes);\n\nif (is_resource($process)) {\n    while ($line = fgets($pipes[1])) {\n        echo "Captured output: $line";\n        // Process the output line by line\n    }\n    fclose($pipes[0]);\n    fclose($pipes[1]);\n    fclose($pipes[2]);\n    proc_close($process);\n}