<?php\n// tracking_pixel.php\n\n// Get the email ID or user ID from the URL\n$email_id = isset($_GET['email_id']) ? intval($_GET['email_id']) : 0;\n\n// Log the email open event (this could be a database insert or a log file entry)\nif ($email_id) {\n    // Connect to your database\n    $mysqli = new mysqli("localhost", "username", "password", "database");\n\n    if ($mysqli->connect_error) {\n        die("Connection failed: " . $mysqli->connect_error);\n    }\n\n    // Log the open event\n    $stmt = $mysqli->prepare("INSERT INTO email_open_logs (email_id, opened_at) VALUES (?, NOW())");\n    $stmt->bind_param("i", $email_id);\n    $stmt->execute();\n    $stmt->close();\n    $mysqli->close();\n}\n\n// Send a 1x1 transparent pixel image\nheader('Content-Type: image/gif');\necho base64_decode('R0lGODlhAQABAIABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==');\n?>