// Class defined by me\nclass User\n{\n    public $fname;\n    public $age;\n\n    /**\n     * @return unknown\n     */\n    public function getfname() {\n        return $this->fname;\n    }\n\n    /**\n     * @return unknown\n     */\n    public function getage() {\n        return $this->age;\n    }\n\n    /**\n     * @param $fname\n     */\n    public function setfname($fname) {\n        $this->fname = $fname;\n    }\n\n    /**\n     * @param $age\n     */\n    public function setage($age) {\n        $this->age = $age;\n    }\n}\n\n// Code for Storing\n$objMemcache = new Memcache();\n$objMemcache->connect('127.0.0.1', 11211);\n\n$obj = new User();\n$obj->setfname('John');\n$obj->setage(32);\n\n$objMemcache->set('user1', $obj, false, 60);\n\n$obj1 = new User();\n$obj1->setfname('Doe');\n$obj1->setage(23);\n\n$objMemcache->set('user2', $obj1, false, 60);\n\nvar_dump($objMemcache->get('user1'));\n\n// Encode to JSON\n$json = json_encode($obj);\necho $json;\n?>