$values = mysql_query("SELECT lname, fname, email, dtelephone, etelephone, contactwhen, thursday, friday, saturday, sunday, monday, comments FROM volunteers_2009 WHERE venue_id = $venue_id");\n\n$csv_output = '';\n\nwhile ($rowr = mysql_fetch_row($values)) {\n    $row = array_map(function($field) {\n        // Escape double quotes by doubling them\n        $field = str_replace('"', '""', $field);\n        // Enclose the field in double quotes\n        return '"' . $field . '"';\n    }, $rowr);\n\n    // Join all fields by comma\n    $csv_output .= implode(',', $row) . "\n";\n}\n\n// Output or save $csv_output as needed