<?php\n// create_module.php\nfunction createModule($moduleName) {\n    $baseDir = __DIR__ . '/module/' . $moduleName;\n    \n    // Create directory structure\n    mkdir($baseDir . '/src/Controller', 0777, true);\n    mkdir($baseDir . '/src/Model', 0777, true);\n    mkdir($baseDir . '/view/' . strtolower($moduleName), 0777, true);\n    \n    // Create a basic Module.php file\n    $moduleFileContent = <<<PHP\n<?php\nnamespace $moduleName;\n\nclass Module\n{\n    public function getConfig()\n    {\n        return include __DIR__ . '/../config/module.config.php';\n    }\n}\nPHP;\n    file_put_contents($baseDir . '/src/Module.php', $moduleFileContent);\n    \n    echo "Module $moduleName created successfully.\n";\n}\n\n$moduleName = $argv[1];\ncreateModule($moduleName);