<?php\n// Read the content of the file first\n$arr = file('test.txt');\n\nif ($arr === false) {\n    die("Failed to read the file");\n}\n\n// Open the file in write mode\n$filec = fopen('test.txt', 'w');\n\nif ($filec === false) {\n    die("Failed to open the file");\n}\n\n// Write the content back to the file\nforeach ($arr as $key => $value) {\n    fwrite($filec, $value);\n}\n\n// Close the file\nfclose($filec);\n?>