class DAL {\n  protected $username;   // the username for db connect \n  protected $pwd;        // the pwd to use when connecting \n  protected $host;       // the host to which one connects \n  protected $dbname;     // the db to select \n  protected $conn;       // reference to the db link resource \n  protected $query_result; // the stored result of the last query you ran \n\n  public function __construct() {\n    $this->username = "your_username";\n    $this->pwd = "your_password";\n    $this->host = "your_host";\n    $this->dbname = "your_dbname";\n  }\n\n  public function connect() {\n    // using mysqli to connect to the database\n    $this->conn = new mysqli($this->host, $this->username, $this->pwd, $this->dbname);\n\n    // Check connection\n    if ($this->conn->connect_error) {\n      die("Connection failed: " . $this->conn->connect_error);\n    }\n  }\n\n  protected function query($sql) {\n    // Executes the query and stores the result in $this->query_result\n    $this->query_result = $this->conn->query($sql);\n    if ($this->query_result === FALSE) {\n      die('Error: ' . $this->conn->error);\n    }\n  }\n\n  public function getAllCountries() {\n    $sql = "\n      SELECT id, name\n      FROM country";\n\n    // Process query\n    $this->query($sql);\n\n    if ($this->query_result) {\n      return $this->query_result;\n    }\n  }\n}\n\nclass myOtherClass extends DAL {\n\n  public function __construct() {\n    parent::__construct();\n    parent::connect();\n  }\n\n  public function getCountryListBox() {\n    $result = parent::getAllCountries();\n\n    if ($result) {\n      $selectbox = "<select id='countryListBox' name='countryListBox'>";\n\n      while ($row = $result->fetch_assoc()) {\n        $selectbox .= "<option value='" . htmlspecialchars($row['id'], ENT_QUOTES, 'UTF-8') . "'>" . htmlspecialchars($row['name'], ENT_QUOTES, 'UTF-8') . "</option>";\n      }\n      $selectbox .= "</select>";\n    } else {\n      $selectbox = "Countries could not be retrieved from database.";\n    }\n\n    return $selectbox;\n  }\n}\n\n// In your template:\n$sl = new myOtherClass();\n\necho '<form id="label_data_form">';\necho $sl->getCountryListBox(); \necho '</form>';