<?php\nif (isset($_GET['p']) && $_GET['p'] == 'view-member' && isset($_GET['username'])) {\n    $username = $_GET['username'];\n    // Load the member profile using the username\n    // Example: fetch member details from the database\n    $member = getMemberDetailsByUsername($username);\n    if ($member) {\n        // Display member profile\n        echo "Profile of " . htmlspecialchars($member['name']);\n    } else {\n        echo "Member not found.";\n    }\n} else {\n    // Handle other pages or show a default page\n    echo "Welcome to the membership site.";\n}\n\nfunction getMemberDetailsByUsername($username) {\n    // Replace with actual database query\n    $members = [\n        'john_doe' => ['name' => 'John Doe', 'id' => 1],\n        'jane_smith' => ['name' => 'Jane Smith', 'id' => 2],\n    ];\n    return isset($members[$username]) ? $members[$username] : null;\n}\n?>