<?php\n// Include Sphinx API\nrequire("sphinxapi.php");\n\n// Create a SphinxClient instance\n$cl = new SphinxClient();\n\n// Set server details\n$cl->SetServer("localhost", 9312);\n\n// Perform the query\n$result = $cl->Query("search term", "books");\n\nif ($result === false) {\n    echo "Query failed: " . $cl->GetLastError();\n} else {\n    if (isset($result["matches"])) {\n        foreach ($result["matches"] as $doc => $docinfo) {\n            // Fetch the stored title directly from the result\n            echo "Title: " . $docinfo['attrs']['title'] . "\n";\n        }\n    }\n}\n?>