<?php\n// Create the user and their public_html directory\nexec("useradd -d /home/username -m username");\nexec("mkdir /home/username/public_html");\n\n// Append the configuration to smb.conf\n$smbConfig = "\n[public_html]\n   path = /home/username/public_html\n   valid users = username\n   read only = no\n";\n\nexec("echo '$smbConfig' >> /etc/samba/smb.conf");\n\n// Restart the Samba service to apply the changes\nexec("systemctl restart smbd");\n\n?>