class My_Custom_Route extends Zend_Controller_Router_Route\n{\n    public function match($path, $partial = false)\n    {\n        $params = parent::match($path, $partial);\n\n        if ($params && isset($params['category']) && !empty($params['category'])) {\n            // Dynamically set the 'category' default value\n            $this->_defaults['category'] = $params['category'];\n        }\n\n        return $params;\n    }\n\n    public function assemble($data = array(), $reset = false, $encode = false, $partial = false)\n    {\n        if (isset($data['category'])) {\n            $this->_defaults['category'] = $data['category'];\n        }\n\n        return parent::assemble($data, $reset, $encode, $partial);\n    }\n}