function check_value($postID) \n{\n    $ID = $postID;\n    $cookie = $_COOKIE['list_of_IDS'];\n    \n    // Add periods around the ID and the cookie string\n    $cookie = '.' . $cookie . '.';\n    $ID = '.' . $ID . '.';\n\n    $position = strpos($cookie, $ID);\n    echo 'ID:'.$postID.'-Cookie:'.$cookie;\n    if ($position !== false)\n    {\n        echo "ID is in the cookie";\n    }\n}