// Get the order collection\n$orders = Mage::getModel('sales/order')->getCollection();\n\n// Get the underlying database table name for sales orders\n$tableName = $orders->getTable('sales/order');\n\n// Add the custom SQL condition to compare total_paid and grand_total\n$orders->getSelect()->where('total_paid = grand_total');\n\n// Load the collection\n$orders->load();\n\n// Now $orders contains all the orders where total_paid is equal to grand_total