$stmt = $conn->prepare("INSERT INTO customer (first_name, last_name, email, password, gender, city, dob, pin) \n                        VALUES (?, ?, ?, ?, ?, ?, ?, ?)");\n$stmt->bind_param("sssssssi", $first_name, $last_name, $email_add, $password, $gender, $city, $DOB, $pin);\n\nif ($stmt->execute()) {\n    echo "New record created successfully";\n} else {\n    echo "Error: " . $stmt->error;\n}\n$stmt->close();