<?php\n\n    use Monolog\Logger;\n    use Monolog\Handler\StreamHandler;\n    use Monolog\Processor\UidProcessor;\n\n    // Create a new Logger instance\n    $log = new Logger('app');\n\n    // Create and push a handler (e.g., StreamHandler)\n    $log->pushHandler(new StreamHandler(__DIR__.'/app.log', Logger::DEBUG));\n\n    // Add a processor to include a unique identifier in each log entry\n    $log->pushProcessor(new UidProcessor());\n\n    // Add context to your log messages\n    $requestId = uniqid();\n    $log->info('User A has created invoice', ['request_id' => $requestId, 'user_id' => 'A', 'invoice_id' => 'XXX']);\n\n    // More log entries with the same context\n    $log->info('User A has approved invoice', ['request_id' => $requestId, 'user_id' => 'A', 'invoice_id' => 'XXX']);\n\n    // Another request\n    $anotherRequestId = uniqid();\n    $log->info('User B has purchased the book', ['request_id' => $anotherRequestId, 'user_id' => 'B', 'book' => 'Some Book']);\n\n    ?>