<?php\n$folder = "apps/";\n\n// Sanitize 't' parameter to prevent directory traversal\nif (!empty($_GET['t'])) {\n    $folder = str_replace("/", "", htmlspecialchars($_GET['t'], ENT_QUOTES, 'UTF-8')) . "/";\n}\n\n// Ensure 'app' parameter is sanitized and defined\n$app = !empty($_GET['app']) ? htmlspecialchars($_GET['app'], ENT_QUOTES, 'UTF-8') : '';\n\nif (empty($app)) {\n    include('apps/home.php');\n} else {\n    // Construct the file path safely\n    $filePath = $folder . basename($app) . ".php";\n    \n    if (file_exists($filePath)) {\n        include($filePath);\n    } else {\n        include("/home/radonsys/public_html/global/error/404.php");\n    }\n}\n?>