<?php\nrequire_once('FirePHPCore/FirePHP.class.php');\nob_start();\n\n$firephp = FirePHP::getInstance(true);\n\n// Simple log message\n$firephp->log('This is a simple log message');\n\n// Log different types of messages\n$firephp->info('This is an info message');\n$firephp->warn('This is a warning message');\n$firephp->error('This is an error message');\n\n// Log arrays and objects\n$firephp->log(array('key1' => 'value1', 'key2' => 'value2'), 'Array Example');\n$firephp->log((object) array('property1' => 'value1', 'property2' => 'value2'), 'Object Example');\n?>