require 'Auth/OpenID/Consumer.php';\nrequire 'Auth/OpenID/FileStore.php';\n\n// Initialize the OpenID consumer\n$store = new Auth_OpenID_FileStore('/tmp/_php_consumer_test');\n$consumer = new Auth_OpenID_Consumer($store);\n\n// Assume $openid_url is the user-provided OpenID URL\n$openid_url = 'http://mysite.com';\n\n// Begin the authentication process\n$auth_request = $consumer->begin($openid_url);\n\nif (!$auth_request) {\n    echo "Authentication error; not a valid OpenID.";\n} else {\n    // Retrieve the normalized OpenID identifier\n    $normalized_url = $auth_request->endpoint->claimed_id;\n    echo "Normalized URL: " . htmlspecialchars($normalized_url, ENT_QUOTES, 'UTF-8');\n}