<?php\n// Define the category ID and the number of posts per group\n$category_id = 1;\n$posts_per_group = 3;\n\n// Fetch all posts from the specified category\n$args = array(\n    'category' => $category_id,\n    'posts_per_page' => -1, // Retrieve all posts in the category\n    'orderby' => 'title',\n    'order' => 'ASC'\n);\n$postslist = get_posts($args);\n\nif ($postslist) {\n    $count = 0;\n    echo '<ul>';\n    foreach ($postslist as $post) {\n        setup_postdata($post);\n        \n        // Open a new <li> tag for every 3 posts\n        if ($count % $posts_per_group == 0) {\n            if ($count > 0) {\n                echo '</li>';\n            }\n            echo '<li>';\n        }\n        \n        // Display the post title\n        echo get_the_title($post);\n\n        // Add separator between post titles\n        $count++;\n        if ($count % $posts_per_group != 0 && $count != count($postslist)) {\n            echo ' | ';\n        }\n    }\n    \n    // Close the last <li> tag\n    echo '</li>';\n    echo '</ul>';\n    \n    // Reset post data\n    wp_reset_postdata();\n} else {\n    echo 'No posts found in this category.';\n}\n?>