<?php\n// Define the paths to your PHP scripts\n$scripts = [\n    'path/to/script1.php',\n    'path/to/script2.php',\n    'path/to/script3.php',\n];\n\n// Loop through each script and execute it\nforeach ($scripts as $script) {\n    $command = 'php ' . escapeshellarg($script);\n    exec($command, $output, $return_var);\n\n    // Check if the script executed successfully\n    if ($return_var !== 0) {\n        echo "Error executing $script\n";\n        // Optionally, handle the error (e.g., log it, halt execution, etc.)\n    } else {\n        // Optionally, process the output if needed\n        echo "Output of $script:\n";\n        echo implode("\n", $output) . "\n";\n    }\n}\n?>