function isUserBehindProxy() {\n    $proxyHeaders = [\n        'HTTP_X_FORWARDED_FOR',\n        'HTTP_X_FORWARDED',\n        'HTTP_FORWARDED_FOR',\n        'HTTP_FORWARDED',\n        'HTTP_VIA',\n        'HTTP_X_COMING_FROM',\n        'HTTP_COMING_FROM',\n        'HTTP_CLIENT_IP'\n    ];\n\n    foreach ($proxyHeaders as $header) {\n        if (isset($_SERVER[$header])) {\n            return true;\n        }\n    }\n\n    return false;\n}\n\nif (isUserBehindProxy()) {\n    echo "User is behind a proxy.";\n} else {\n    echo "User is not behind a proxy.";\n}