<?php\n$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n$x = 0;\nforeach ($stuff as $key) {\n    $stmt3 = $pdo->prepare("SELECT * FROM foobar WHERE id = :id");\n    $stmt3->bindParam(':id', $key, PDO::PARAM_INT);\n    $stmt3->execute();\n\n    while ($data3 = $stmt3->fetch(PDO::FETCH_ASSOC)) {\n        $stmt4 = $pdo->prepare("SELECT * FROM foobar_img WHERE id = :id");\n        $stmt4->bindParam(':id', $data3['id'], PDO::PARAM_INT);\n        $stmt4->execute();\n\n        while ($data4 = $stmt4->fetch(PDO::FETCH_ASSOC)) {\n            $x += 1;\n            if ($x % 3 == 0) {\n                echo "a: " . $data3['column_name']; // replace 'column_name' with the actual column name\n            } else {\n                echo "b: " . $data3['column_name']; // replace 'column_name' with the actual column name\n            }\n        }\n    }\n}\n?>