<?php\n// Load the HTML content\n$html = <<<HTML\n<div id="interestingbox">\n   <div id="interestingdetails" class="txtnormal">\n        <div>Content1</div>\n        <div>Content2</div>\n   </div>\n</div>\n\n<div id="interestingbox">\n   <div id="interestingdetails" class="txtnormal">\n        <div>Content3</div>\n        <div>Content4</div>\n   </div>\n</div>\nHTML;\n\n// Create a new DOMDocument instance\n$dom = new DOMDocument;\n\n// Suppress warnings due to malformed HTML\nlibxml_use_internal_errors(true);\n\n// Load the HTML\n$dom->loadHTML($html);\n\n// Restore error handling\nlibxml_clear_errors();\n\n// Create a new DOMXPath instance\n$xpath = new DOMXPath($dom);\n\n// Query for all div elements with id 'interestingdetails'\n$elements = $xpath->query('//div[@id="interestingdetails"]');\n\nforeach ($elements as $element) {\n    // Get all child div elements\n    $childDivs = $element->getElementsByTagName('div');\n    foreach ($childDivs as $childDiv) {\n        echo $childDiv->nodeValue . "\n";\n    }\n}\n?>