<?php\nfunction displayFirstImageFromFolders($directory, $defaultImage) {\n    // Ensure the directory exists\n    if (!is_dir($directory)) {\n        echo "Directory does not exist.";\n        return;\n    }\n\n    // Scan the main directory for subdirectories\n    $folders = array_filter(glob($directory . '/*'), 'is_dir');\n\n    // Loop through each folder\n    foreach ($folders as $folder) {\n        $images = glob($folder . '/*.jpg');\n        \n        if (!empty($images)) {\n            // Display the first image found\n            echo '<img src="' . $images[0] . '" alt="Image from ' . $folder . '">';\n        } else {\n            // Display the default image if no images found\n            echo '<img src="' . $defaultImage . '" alt="Default Image">';\n        }\n    }\n}\n\n$directory = 'path/to/your/folder'; // Replace with the path to your folder\n$defaultImage = 'path/to/default/image.jpg'; // Replace with the path to your default image\ndisplayFirstImageFromFolders($directory, $defaultImage);\n?>