<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $action = $_POST['action'];\n    if (in_array($action, ['start', 'stop'], true)) {\n        $output = shell_exec("sudo systemctl $action myapp.service");\n        echo "Service $action requested: " . htmlspecialchars($output);\n    } else {\n        echo "Invalid action.";\n    }\n} else {\n    echo "Invalid request method.";\n}\n?>