$rows = array();\n$res = mysql_query($someQuery);\n\nif ($res === FALSE) {\n    // Query failed, handle the error\n    echo "MySQL error: " . mysql_error();\n} else {\n    if (is_resource($res) && mysql_num_rows($res) > 0) {\n        // Query was a SELECT statement with results\n        while ($row = mysql_fetch_assoc($res)) {\n            $rows[] = $row;\n        }\n    } else {\n        // Query was a DELETE/INSERT/UPDATE statement or SELECT with no results\n        // Handle accordingly if needed\n    }\n}