$sql = "SELECT myTable.*\n        FROM myTable\n        JOIN search_index ON myTable.id = search_index.id\n        JOIN category ON myTable.category_id = category.id\n        JOIN geographic_location ON myTable.location_id = geographic_location.id\n        WHERE MATCH(search_index.title, search_index.description, search_index.keywords) \n        AGAINST (? IN BOOLEAN MODE) AND myTable.status = 'live'\n        AND category.name = ?\n        AND geographic_location.distance <= ?\n        ORDER BY myTable.some_column";\n\n$stmt = $pdo->prepare($sql);\n$stmt->execute([$searchQuery, $categoryName, $maxDistance]);\n$results = $stmt->fetchAll();