class FanStruct {\n    public $date; \n    public $userid;\n\n    function __construct($date, $id) {\n        $this->date = $date;\n        $this->userid = $id;\n    }\n}\n\nfunction removeDuplicates($array) {\n    $seen = [];\n    $result = [];\n\n    foreach ($array as $item) {\n        if (!isset($seen[$item->userid])) {\n            $seen[$item->userid] = true;\n            $result[] = $item;\n        }\n    }\n\n    return $result;\n}\n\n// Example usage\n$array = [\n    new FanStruct('2023-10-01', 1),\n    new FanStruct('2023-10-02', 2),\n    new FanStruct('2023-10-03', 1), // Duplicate userid\n    new FanStruct('2023-10-04', 3)\n];\n\n$uniqueArray = removeDuplicates($array);\n\nforeach ($uniqueArray as $item) {\n    echo $item->date . ", " . $item->userid . "\n";\n}