<?php\nsession_start();\n\n// Get or Create Array\nif (isset($_SESSION['sessRView'])) {\n    $RView = $_SESSION['sessRView'];\n} else {\n    $RView = array();\n}\n\n// Assuming $row_rsPrd contains the current product being viewed\n// Example:\n$row_rsPrd = array('PrdID' => '1006', 'RefCode' => 'Ref_06', 'Name' => 'Name_06');\n\n// Create an associative array for the new product\n$newProduct = array(\n    'PrdID'   => $row_rsPrd['PrdID'],\n    'RefCode' => $row_rsPrd['RefCode'],\n    'Name'    => $row_rsPrd['Name']\n);\n\n// Append currently viewed Product to Array\narray_unshift($RView, $newProduct);\n\n// Check if more than 5 products exist in Array, if so delete the oldest.\nif (count($RView) > 5) {\n    array_pop($RView);\n}\n\n// Update Session for next page\n$_SESSION['sessRView'] = $RView;\n\n// Display Array\necho "<ul>";\nfor ($row = 0; $row < count($RView); $row++) {\n    if ($row < 5) { // Ensure we only display up to 5 items\n        echo "<li><a href='?PrdID=".$RView[$row]['PrdID']."'>".$RView[$row]['RefCode']."</a> : ".$RView[$row]['Name']."</li>";\n    }\n}\necho "</ul>";\n?>