<?php\n$keywords = "word1 word2 word3"; // Input keywords\n$searchTerms = explode(" ", $keywords); \n$synonyms = [\n    'word1' => ['synonym1', 'synonym2'],\n    'word2' => ['synonym3', 'synonym4'],\n    // Add more synonyms as needed\n];\n\n$searchQuery = implode(' ', array_map(function($word) use ($synonyms) {\n    return $word . ' ' . implode(' ', $synonyms[$word] ?? []);\n}, $searchTerms));\n\n$sql = "SELECT myTable.*\n        FROM myTable\n        JOIN search_index ON myTable.id = search_index.id\n        WHERE MATCH(search_index.title, search_index.description, search_index.keywords) \n        AGAINST (? IN BOOLEAN MODE) AND myTable.status = 'live'";\n\n$stmt = $pdo->prepare($sql);\n$stmt->execute([$searchQuery]);\n$results = $stmt->fetchAll();\n?>