<?php\n// Define the paths\n$pythonPath = '/usr/python/bin/python3';\n$scriptPath = '/var/www/cgi-bin/tabular.py';\n$outputFile = '/var/www/html/output.txt';\n\n// Ensure the user running the PHP script has the necessary permissions\n$command = escapeshellcmd("$pythonPath $scriptPath 1 > $outputFile 2>&1");\n\n// Run the command\nsystem($command, $retval);\n\n// Check the return value for errors\nif ($retval != 0) {\n    echo "Error occurred while running the Python script. Check the output file for details.";\n} else {\n    echo "Python script executed successfully. Check the output file for details.";\n}\n?>