$html = '<!-- Your HTML content here -->';\n\n$pattern = '/(&|\?)utm_source=report(&utm_medium=email)?(&utm_campaign=report)?/';\n$replacement = '';\n\n$html = preg_replace_callback($pattern, function ($matches) {\n    // If the match starts with a ?, replace it with ?\n    if ($matches[0][0] === '?') {\n        return '?';\n    }\n    // Otherwise, replace it with an empty string\n    return '';\n}, $html);\n\n// Remove any trailing ? or & if they are left over\n$html = preg_replace('/[\?&]+$/', '', $html);\n\necho $html;