class UserDAO {\n    private $db;\n\n    public function __construct(DatabaseInterface $db) {\n        $this->db = $db;\n    }\n\n    public function create(User $user) {\n        $sql = sprintf(\n            "INSERT INTO user (first, last, email) VALUES ('%s', '%s', '%s')",\n            $user->first,\n            $user->last,\n            $user->email\n        );\n        return $this->db->query($sql);\n    }\n\n    // Implement other CRUD operations similarly\n}