$tag = 'team-fortress-2';\n$q = mysql_query("SELECT p.id, p.title, p.text \n                  FROM Posts p\n                  JOIN Posts_tags pt ON p.id = pt.post_id\n                  JOIN Tags t ON pt.tag_id = t.id\n                  WHERE t.name = '" . mysql_real_escape_string($tag) . "'");\nwhile ($r = mysql_fetch_array($q)) {\n    // Process each post\n    echo "ID: " . $r['id'] . "\n";\n    echo "Title: " . $r['title'] . "\n";\n    echo "Text: " . $r['text'] . "\n\n";\n}