<?php\n$name = 'Username'; // Username (MUST start with a capital letter)\n$pass = 'password'; // Password (plaintext, will be hashed later down)\n$email = 'email@example.com'; // Email (automatically gets confirmed after the creation process)\n$path = "/path/to/mediawiki";\n\nputenv("MW_INSTALL_PATH={$path}");\nrequire_once("{$path}/includes/WebStart.php");\n\n// Create a new user\n$user = User::newFromName($name);\nif ($user === null) {\n    echo "Invalid username\n";\n    exit(1);\n}\nif ($user->getId() != 0) {\n    echo "User already exists\n";\n    exit(1);\n}\n\n$user->setPassword($pass);\n$user->setEmail($email);\n$user->addToDatabase();\n$user->saveSettings();\n$user->setToken(); // Set edit token\n\n// Confirm email\n$user->setEmailAuthenticationTimestamp(time());\n$user->saveSettings();\n\n// Update site statistics\n$ssUpdate = new SiteStatsUpdate(0, 0, 0, 0, 1);\n$ssUpdate->doUpdate();\n\necho "User created successfully\n";\n?>