<?php\n// Sample data to mimic the result from the MySQL query\n$items = [\n    ['id' => 1, 'title' => 'A', 'sequence_number' => 1, 'depth' => 1],\n    ['id' => 2, 'title' => 'B', 'sequence_number' => 2, 'depth' => 1],\n    ['id' => 3, 'title' => 'C', 'sequence_number' => 1, 'depth' => 2],\n    ['id' => 4, 'title' => 'D', 'sequence_number' => 3, 'depth' => 1],\n    ['id' => 5, 'title' => 'E', 'sequence_number' => 4, 'depth' => 1],\n    ['id' => 6, 'title' => 'F', 'sequence_number' => 2, 'depth' => 2],\n    ['id' => 7, 'title' => 'A', 'sequence_number' => 1, 'depth' => 3],\n    ['id' => 8, 'title' => 'B', 'sequence_number' => 5, 'depth' => 1],\n    ['id' => 9, 'title' => 'C', 'sequence_number' => 3, 'depth' => 2],\n    ['id' => 10, 'title' => 'D', 'sequence_number' => 2, 'depth' => 3],\n    ['id' => 11, 'title' => 'E', 'sequence_number' => 6, 'depth' => 1],\n    ['id' => 12, 'title' => 'F', 'sequence_number' => 4, 'depth' => 2],\n];\n\n// Function to build the multilevel menu\nfunction buildMenu($items, $depth = 1) {\n    $output = '<ul>';\n    foreach ($items as $item) {\n        if ($item['depth'] == $depth) {\n            $output .= '<li>' . $item['title'];\n            // Check if there are any children\n            $children = array_filter($items, function($child) use ($item) {\n                return $child['depth'] > $item['depth'] && strpos($child['sequence_number'], $item['sequence_number']) === 0;\n            });\n            if (!empty($children)) {\n                $output .= buildMenu($children, $depth + 1);\n            }\n            $output .= '</li>';\n        }\n    }\n    $output .= '</ul>';\n    return $output;\n}\n\n// Sort items by sequence_number and depth\nusort($items, function($a, $b) {\n    return $a['sequence_number'] <=> $b['sequence_number'] ?: $a['depth'] <=> $b['depth'];\n});\n\n// Generate the menu\necho buildMenu($items);\n?>