$conn = mysqli_connect('localhost', 'mysql_user', 'mysql_password', 'norwegianfashion');\n\n// Check connection\nif (!$conn) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n// Set the character set to UTF-8\nmysqli_set_charset($conn, 'utf8');\n\n// Perform the query\n$name = mysqli_real_escape_string($conn, "Fióre`s måløye");\n$query = "SELECT id FROM myTable WHERE name = '$name'";\n$result = mysqli_query($conn, $query);\n\nif ($result) {\n    while ($row = mysqli_fetch_assoc($result)) {\n        // Process the result\n        echo "ID: " . $row['id'] . "\n";\n    }\n} else {\n    echo "Error: " . mysqli_error($conn);\n}\n\n// Close the connection\nmysqli_close($conn);