<?php\n$xmlDoc = new DOMDocument();\n$xmlDoc->load("testrtap11.xml");\n\n// Create MySQL connection using mysqli\n$con = new mysqli("hostname", "username", "password", "zeeshan_database1");\n\n// Check connection\nif ($con->connect_error) {\n    die("Connection failed: " . $con->connect_error);\n}\n\n$x = $xmlDoc->getElementsByTagName('RECORD');\n$z = $xmlDoc->getElementsByTagName('TITLE');\n$w = $xmlDoc->getElementsByTagName('PRIMARY_AUTHOR');\n$y = $xmlDoc->getElementsByTagName('JOURNAL_CONFERENCE');\n\nfor ($i = 0; $i < $x->length; $i++) {\n    $zz = $z->item($i)->nodeValue;\n    $ww = $w->item($i)->nodeValue;\n    $yy = $y->item($i)->nodeValue;\n\n    // Prepare statement to prevent SQL injection\n    $stmt = $con->prepare("INSERT INTO Persons (FirstName, LastName, Age) VALUES (?, ?, ?)");\n    $stmt->bind_param("sss", $zz, $ww, $yy);\n\n    if (!$stmt->execute()) {\n        die('Error: ' . $stmt->error);\n    }\n    echo "1 record added\n";\n    \n    $stmt->close();\n}\n\n$con->close();\n?>