// CustomRequest.php\nclass CustomRequest extends Zend_Controller_Request_Http\n{\n    public function getParam($key, $default = null)\n    {\n        $value = parent::getParam($key, $default);\n        return $this->filter($value);\n    }\n\n    protected function filter($value)\n    {\n        // Apply your filtering logic here\n        $value = htmlspecialchars($value, ENT_QUOTES, 'UTF-8'); // Escape HTML tags\n        $value = filter_var($value, FILTER_SANITIZE_STRING); // Sanitize string\n        // Add any additional filtering logic here (e.g., SQL escaping if necessary)\n        return $value;\n    }\n}