use Zend\Http\Client;\n\nfunction getWikipediaInfo($searchTerm) {\n    $client = new Client();\n    $client->setUri('https://en.wikipedia.org/w/api.php');\n    $client->setMethod('GET');\n    $client->setParameterGet(array(\n        'action' => 'query',\n        'format' => 'json',\n        'prop' => 'extracts|pageprops',\n        'exintro' => true,\n        'explaintext' => true,\n        'titles' => $searchTerm\n    ));\n\n    $response = $client->send();\n    if ($response->isSuccess()) {\n        $data = json_decode($response->getBody(), true);\n        $pages = $data['query']['pages'];\n\n        foreach ($pages as $page) {\n            if (isset($page['pageprops']['disambiguation'])) {\n                // Handle disambiguation\n                return handleDisambiguation($page['title']);\n            } elseif (isset($page['extract'])) {\n                return $page['extract'];\n            } else {\n                return "No information found for $searchTerm.";\n            }\n        }\n    } else {\n        return "Error fetching data from Wikipedia.";\n    }\n}\n\nfunction handleDisambiguation($title) {\n    // You might need to fetch the disambiguation page and process the links\n    return "The term '$title' is ambiguous. Please specify further.";\n}\n\n// Example usage\n$info = getWikipediaInfo('Stadium');\necho $info;