<?php\n// Database connection using PDO\n$dsn = 'mysql:host=your_host;dbname=your_dbname;charset=utf8';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // SQL query to fetch categories and their forums\n    $sql = "SELECT categories.id AS category_id, categories.name AS category_name, forums.id AS forum_id, forums.name AS forum_name, forums.description\n            FROM categories\n            LEFT JOIN forums ON categories.id = forums.cat_id\n            ORDER BY categories.position, forums.position";\n    \n    // Prepare and execute the query\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute();\n    \n    // Fetch all results\n    $results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    \n    // Initialize an array to store the structured data\n    $data = [];\n    \n    // Structure the data\n    foreach ($results as $row) {\n        $category_id = $row['category_id'];\n        if (!isset($data[$category_id])) {\n            $data[$category_id] = [\n                'name' => $row['category_name'],\n                'forums' => []\n            ];\n        }\n        if ($row['forum_id']) {\n            $data[$category_id]['forums'][] = [\n                'id' => $row['forum_id'],\n                'name' => $row['forum_name'],\n                'description' => $row['description']\n            ];\n        }\n    }\n    \n    // Display the categories and forums\n    foreach ($data as $category) {\n        echo "<h2>" . htmlspecialchars($category['name']) . "</h2>";\n        if (!empty($category['forums'])) {\n            echo "<ul>";\n            foreach ($category['forums'] as $forum) {\n                echo "<li>";\n                echo "<h3>" . htmlspecialchars($forum['name']) . "</h3>";\n                echo "<p>" . htmlspecialchars($forum['description']) . "</p>";\n                echo "</li>";\n            }\n            echo "</ul>";\n        } else {\n            echo "<p>No forums available.</p>";\n        }\n    }\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>