<?php\n// A list of allowed pages to prevent open redirects\n$allowed_pages = ['home', 'about', 'contact']; \n\n// Get the page parameter and sanitize it\n$page = isset($_GET['page']) ? $_GET['page'] : 'home';\n$page = filter_var($page, FILTER_SANITIZE_STRING);\n\n// Validate the page against the allowed list\nif (in_array($page, $allowed_pages)) {\n    header("Location: " . htmlspecialchars($page));\n} else {\n    // Default to a safe page or show an error\n    header("Location: home");\n    // Or show a 404 error\n    // header("HTTP/1.0 404 Not Found");\n    // echo "Page not found.";\n    exit();\n}\n?>