function encryptData($data, $encryption_key) {\n    $cipher = "aes-256-cbc"; // Cipher method\n    $ivlen = openssl_cipher_iv_length($cipher);\n    $iv = openssl_random_pseudo_bytes($ivlen);\n    $encrypted = openssl_encrypt($data, $cipher, $encryption_key, 0, $iv);\n    return base64_encode($iv . $encrypted);\n}\n\n$data = "This is the data to be encrypted";\n$encryption_key = "your-secret-encryption-key"; // 32 bytes for AES-256\n$encrypted_data = encryptData($data, $encryption_key);\n\necho $encrypted_data;