<?php\n$options = [\n    1 => 'Option 1',\n    2 => 'Option 2',\n    3 => 'Option 3'\n];\n\n$t = isset($_GET['t']) ? $_GET['t'] : null; // Check if 't' is set in $_GET\n\nforeach ($options as $k => $v) {\n    echo '<option value="http://domain.com/' . htmlspecialchars($k) . '/"';\n    if ($k == $t) {\n        echo ' selected';\n    }\n    echo '>' . htmlspecialchars($v) . '</option>';\n}\n?>