<?php\n// Database connection details\n$host = 'localhost';\n$db = 'your_database';\n$user = 'your_username';\n$pass = 'your_password';\n$charset = 'utf8mb4';\n\n$dsn = "mysql:host=$host;dbname=$db;charset=$charset";\n$options = [\n    PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n    PDO::ATTR_EMULATE_PREPARES   => false,\n];\n\ntry {\n    $pdo = new PDO($dsn, $user, $pass, $options);\n} catch (\PDOException $e) {\n    throw new \PDOException($e->getMessage(), (int)$e->getCode());\n}\n\n// Parameterized SELECT query\n$name = 'some_name';\n$sql = "SELECT ID FROM your_table WHERE name = :name";\n$stmt = $pdo->prepare($sql);\n$stmt->bindParam(':name', $name, PDO::PARAM_STR);\n$stmt->execute();\n$id = $stmt->fetchColumn();\n\nif ($id) {\n    // Use the ID for an INSERT into another table\n    $insertSql = "INSERT INTO another_table (column_name) VALUES (:id)";\n    $insertStmt = $pdo->prepare($insertSql);\n    $insertStmt->bindParam(':id', $id, PDO::PARAM_INT);\n\n    if ($insertStmt->execute()) {\n        echo "Insert successful!";\n    } else {\n        echo "Insert failed!";\n    }\n} else {\n    echo "No matching ID found.";\n}\n?>